import { useState } from "react";
import { MovieCard } from "../movie-card/movie-card";
import { MovieView } from "../movie-view/movie-view";

export const MainView = () => {
  const [movies, setMovies] = useState([
    {
      _id: '1',
      Title: 'Inception',
      Description: 'A thief who steals corporate secrets through dream-sharing technology.',
      Genre: { Name: 'Sci-Fi' },
      Director: { Name: 'Christopher Nolan', Bio: 'British-American filmmaker.' },
      ImagePath: 'https://m.media-amazon.com/images/I/91kFYg4fX3L._AC_SY679_.jpg',
    },
    {
      _id: '2',
      Title: 'Interstellar',
      Description: "A team of explorers travel through a wormhole in space in an attempt to ensure humanity's survival.",
      Genre: { Name: 'Sci-Fi' },
      Director: { Name: 'Christopher Nolan', Bio: 'British-American filmmaker.' },
      ImagePath: 'https://m.media-amazon.com/images/I/71qGcH5CDBL._AC_SY679_.jpg',
    },
    {
      _id: '3',
      Title: 'The Dark Knight',
      Description: "When the menace known as the Joker emerges, Batman must face his greatest psychological and physical tests.",
      Genre: { Name: 'Action' },
      Director: { Name: 'Christopher Nolan', Bio: 'British-American filmmaker.' },
      ImagePath: 'https://m.media-amazon.com/images/I/71pVtUFT2-L._AC_SY679_.jpg',
    }
  ]);

  const [selectedMovie, setSelectedMovie] = useState(null);

  // Render MovieView when a movie is selected
  if (selectedMovie) {
    return <MovieView movie={selectedMovie} onBack={() => setSelectedMovie(null)} />;
  }

  // Display MovieCard components for each movie in the movies array
  return (
    <div className="movie-list">
      {movies.map((movie) => (
        <MovieCard key={movie._id} movie={movie} onClick={() => setSelectedMovie(movie)} />
      ))}
    </div>
  );
};
